module testbench;
    reg clk, reset;
    wire [3:0] q;
    integer i;

    // Device Under Test
    top_module DUT (
        .clk(clk),
        .reset(reset),
        .q(q)
    );

    // Clock generation
    always begin
        clk = 1'b0;
        #5; // clock period = 10 ns
        clk = 1'b1;
        #5;
    end

    // Test scenario
    initial begin
        // Monitor changes in output
        $monitor("Time=%g, Reset=%b, Output Q=%b", $time, reset, q);

        // Initialize reset
        reset = 1'b0;
        #10;

        // Assert reset
        reset = 1'b1;
        #10; // allow time for reset to take effect

        // Deassert reset
        reset = 1'b0;

        // Check reset functionality
        if (q !== 4'd1) begin
            $display("Test case failed: Reset functionality");
            $finish;
        end

        // Check count sequence
        for (i = 1; i <= 10; i = i + 1) begin
            #10; // wait for one clock cycle
            if (q !== i[3:0]) begin
                $display("Test case failed at count %d, expected %d, got %d", i, i, q);
                $finish;
            end
        end

        // Check wrap around to 1
        #10;
        if (q !== 4'd1) begin
            $display("Test case failed: Counter does not wrap back to 1");
            $finish;
        end

        // Display success message if all tests pass
        $display("all test cases passed");

        $finish;
    end
endmodule

