module testbench;

    reg clk;
    reg areset;
    reg train_valid;
    reg train_taken;
    wire [1:0] state;

    top_module dut(
        .clk(clk),
        .areset(areset),
        .train_valid(train_valid),
        .train_taken(train_taken),
        .state(state)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // Clock period of 10ns
    end

    // Test cases
    initial begin
        areset = 1;
        #10;
        areset = 0;
        train_valid = 0;
        train_taken = 0;

        // Case 1: Reset check
        #10;
        if (state != 2'b01) $display("Reset test case failed.");

        // Case 2: Increment to max
        train_valid = 1; train_taken = 1;
        #10; // state should be 10
        #10; // state should be 11
        #10; // state should max out at 11
        if (state != 2'b11) $display("Increment max test case failed.");

        // Case 3: Decrement to min
        train_taken = 0;
        #10; // state should be 10
        #10; // state should be 00
        #10; // state should remain 00
        if (state != 2'b00) $display("Decrement min test case failed.");

        // Case 4: Maintain state when not training
        train_valid = 0;
        #20; // No change in state
        if (state != 2'b00) $display("Maintain state test case failed.");

        // Case 5: Back to normal operation
        train_valid = 1; train_taken = 1;
        #10; // state should be 01
        #10; // state should be 10
        if (state != 2'b10) $display("Normal operation test case failed.");

        // Final check for all test cases passed
        if (!$test$plusargs("failed")) $display("all test cases passed");

        // Finish the simulation
        $finish;
    end
endmodule

