    integer error_count = 0;

    initial begin
        // Test all possible select lines
        for (i = 0; i < 256; i = i + 1) begin
            in = 256'b0;  // Reset inputs
            in[i] = 1'b1; // Set only the i-th bit to 1
            sel = i;      // Set the select line to i

            // Apply the inputs
            #10;

            // Check if the output is as expected
            if (out !== 1'b1) begin
                $display("Test case failed for sel = %d", i);
                error_count = error_count + 1;
            end
        end

        // Check if there are any errors
        if (error_count == 0) begin
            $display("All test cases passed");
        end

        // Finish the simulation
