module top_module_tb;
    reg clk, reset, data;
    wire start_shifting;

    // Instantiate the DUT
    top_module DUT (
        .clk(clk),
        .reset(reset),
        .data(data),
        .start_shifting(start_shifting)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // 100MHz Clock
    end

    // Reset generation
    initial begin
        reset = 1;
        #20 reset = 0; // Release reset after 20ns
    end

    // Test scenarios
    integer err = 0;

    initial begin
        // Initialize data
        data = 0;

        // Apply reset
        @(posedge clk); #1;
        reset = 1; @(posedge clk); #1;
        reset = 0;

        // Sequence does not match
        data = 0; @(posedge clk); #1; // 0
        data = 1; @(posedge clk); #1; // 01
        data = 1; @(posedge clk); #1; // 011
        data = 0; @(posedge clk); #1; // 0110
        if (start_shifting !== 0) err = 1;

        // Valid sequence
        data = 1; @(posedge clk); #1; // 1101
        @(posedge clk); #1; // start_shifting should be high
        if (start_shifting !== 1) err = 1;

        // Keep applying more non-reseting inputs
        data = 0; @(posedge clk); #1;
        data = 1; @(posedge clk); #1;
        if (start_shifting !== 1) err = 1;

        // Test Reset
        reset = 1; @(posedge clk); #1;
        reset = 0;
        if (start_shifting !== 0) err = 1;

        // Sequence at different phases
        data = 1; @(posedge clk); #1; // 1
        data = 1; @(posedge clk); #1; // 11
        data = 0; @(posedge clk); #1; // 110
        data = 1; @(posedge clk); #1; // 1101
        if (start_shifting !== 1) err = 1;

        // Finish test and check all cases
        #10;
        if (err == 0) begin
            $display("all test cases passed");
        end
        $finish;
    end
endmodule

