    reg [7:0] test_vector[0:15];  // Array of test vectors
    reg correct;
    integer i;

    initial begin
        // Initialize test vectors
        test_vector[0]  = 8'b0000_0000;
        test_vector[1]  = 8'b0000_0001;
        test_vector[2]  = 8'b0000_0010;
        test_vector[3]  = 8'b0000_0100;
        test_vector[4]  = 8'b0000_1000;
