        test_vector[7]  = 8'b0100_0000;
        test_vector[8]  = 8'b1000_0000;
        test_vector[9]  = 8'b1111_0000;
        test_vector[10] = 8'b0000_1111;
        test_vector[11] = 8'b1111_1111;
        test_vector[12] = 8'b1010_1010;
        test_vector[13] = 8'b0101_0101;
        test_vector[14] = 8'b1100_1100;
