module testbench;

  reg clk;
  reg resetn;
  reg [1:0] byteena;
  reg [15:0] d;
  wire [15:0] q;

  top_module dut(
    .clk(clk),
    .resetn(resetn),
    .byteena(byteena),
    .d(d),
    .q(q)
  );

  // Clock generation
  initial begin
    clk = 0;
    forever #5 clk = ~clk; // Toggle clock every 5ns
  end

  integer i;
  reg [15:0] expected_value;
  reg all_tests_passed;

  initial begin
    resetn = 1;
    all_tests_passed = 1'b1;
    #10;

    // Simulate synchronous reset
    resetn = 0;
    #10;
    resetn = 1;
    #10;

    // Test Case 1: All bytes enabled, full write
    byteena = 2'b11;
    d = 16'hA5A5;
    expected_value = 16'hA5A5;
    #10;
