module testbench;

  reg [3:0] in;
  wire [2:0] out_both;
  wire [3:1] out_any;
  wire [3:0] out_different;
  integer i;
  reg all_passed;

  // Instantiate the Device Under Test (DUT)
  top_module DUT (
    .in(in),
    .out_both(out_both),
    .out_any(out_any),
    .out_different(out_different)
  );

  initial begin
    // Initialize all inputs
    in = 4'b0000;
    all_passed = 1;

    // Test Cases
    for (i = 0; i < 16; i = i + 1) begin
      in = i;  // cycling through all 4-bit combinations
      #10;

      // Check out_both
      if (i < 15)  // We only check indices 0 to 2
        if (out_both !== {in[2] & in[3], in[1] & in[2], in[0] & in[1]}) begin
          all_passed = 0;
        end

      // Check out_any
      if (i > 0)  // We only check indices 1 to 3
        if (out_any !== {in[3] | in[2], in[2] | in[1], in[1] | in[0]}) begin
          all_passed = 0;
        end

      // Check out_different by treating input as wrapping around.
      if (out_different !== {(in[3] ^ in[0]), (in[2] ^ in[3]), (in[1] ^ in[2]), (in[0] ^ in[1])}) begin
        all_passed = 0;
      end
    end

    if (all_passed) begin
      $display("all test cases passed");
    end

    $finish;
