module testbench;
    // Inputs
    reg clk;
    reg areset;
    reg bump_left;
    reg bump_right;
    reg ground;

    // Outputs
    wire walk_left;
    wire walk_right;
    wire aaah;

    // Instantiate the Unit Under Test (UUT)
    top_module uut (
        .clk(clk),
        .areset(areset),
        .bump_left(bump_left),
        .bump_right(bump_right),
        .ground(ground),
        .walk_left(walk_left),
        .walk_right(walk_right),
        .aaah(aaah)
    );

    // Clock generation
    initial begin
        int count = 0;
        clk = 0;
        while (count < 1000) begin  // Limiting the number of clock cycles
            #5 clk = ~clk;
            count = count + 1;
        end
    end

    // Test cases
    initial begin
        // Initialize Inputs
        areset = 1; bump_left = 0; bump_right = 0; ground = 1;
        #10;
        areset = 0;

        // Check initial state, should start walking left
        if (walk_left !== 1 || walk_right !== 0 || aaah !== 0) $display("Test case failed");

        // Test bump_left functionality
        bump_left = 1; #10;
        if (walk_left !== 0 || walk_right !== 1 || aaah !== 0) $display("Test case failed");

        // Reset system
        areset = 1; #10; areset = 0; #10;

        // Test bump_right functionality
        bump_right = 1; #10;
        if (walk_left !== 1 || walk_right !== 0 || aaah !== 0) $display("Test case failed");

        // Reset system
        areset = 1; #10; areset = 0; #10;

        // Test falling functionality
        ground = 0; #10;
        if (walk_left !== 1 || walk_right !== 0 || aaah !== 1) $display("Test case failed");

        // Resuming from fall
        ground = 1; #10;
        if (walk_left !== 1 || walk_right !== 0 || aaah !== 0) $display("Test case failed");

        // Falling then bumped
        ground = 0; bump_left = 1; #10;
        if (walk_left !== 1 || walk_right !== 0 || aaah !== 1) $display("Test case failed");

        // Falling continues
        bump_left = 0; #10;
        if (walk_left !== 1 || walk_right !== 0 || aaah !== 1) $display("Test case failed");

        // Check recovery to walking state after double bump
        ground = 1; bump_left = 1; bump_right = 1; #10;
        if (walk_left !== 0 || walk_right !== 1 || aaah !== 0) $display("Test case failed");

        // All tests done
        #10;
        if (!$isunknown(walk_left) && !$isunknown(walk_right) && !$isunknown(aaah))
            $display("all test cases passed");
    end
endmodule

