        d = 0;
        ena = 0;

        // Following set of vectors will test the functionality of D latch
        #10 d = 1; ena = 0; // D should not be latched
        #10 check(q, 0);     // q should remain 0 because ena is 0

        #10 ena = 1;         // Enable is set to 1
        #10 check(q, 1);     // Now q should follow d and be 1

        #10 d = 0;           // Change D to 0 with ena still 1
        #10 check(q, 0);     // q should now be 0

        #20 ena = 0;         // Disable latch
        #10 d = 1;           // Change D, q should not be affected
        #10 check(q, 0);     // q should still be 0

        #20 ena = 1;         // Enable the latch again
        #10 check(q, 1);     // D was 1 before enabling, q should latch to 1

        #10 $display("all test cases passed");
        $finish; // End simulation
    end

    task check;
        input logic expected;
        input logic actual;
        if (expected !== actual) begin
            $display("Test case failed: Expected %b, Got %b", expected, actual);
            $finish;
        end
    endtask

