    initial begin
        // Initialize inputs
        a = 16'hAAAA; b = 16'hBBBB; c = 16'hCCCC; d = 16'hDDDD; e = 16'hEEEE;
        f = 16'hFFFF; g = 16'h1111; h = 16'h2222; i_input = 16'h3333;

        // Test each select case
        for (j = 0; j < 16; j = j + 1) begin
            sel = j;
            #10; // Delay for signals to propagate

            case(sel)
                4'd0: if (out !== a) errors = errors + 1;
                4'd1: if (out !== b) errors = errors + 1;
                4'd2: if (out !== c) errors = errors + 1;
                4'd3: if (out !== d) errors = errors + 1;
