                4'd6: if (out !== g) errors = errors + 1;
                4'd7: if (out !== h) errors = errors + 1;
                4'd8: if (out !== i_input) errors = errors + 1;
                default: if (out !== 16'hFFFF) errors = errors + 1;
            endcase
        end

        // Check if all test cases passed
        if (errors === 0)
            $display("all test cases passed");

