            #10; // Wait for edge detection

            if (anyedge !== expected_anyedge) begin
                $display("Mismatch found! Input: %b, Expected anyedge: %b, Output anyedge: %b", in, expected_anyedge, anyedge);
                error_count = error_count + 1;
            end

            #10; // Time for observing changes
        end

        // Checking if all test cases were successful
