module testbench;
    // Inputs
    reg clk;
    reg reset;
    reg in;

    // Outputs
    wire disc;
    wire flag;
    wire err;

    // Instantiate the Unit Under Test (DUT)
    top_module uut (
        .clk(clk),
        .reset(reset),
        .in(in),
        .disc(disc),
        .flag(flag),
        .err(err)
    );

    // Clock generation
    always #5 clk = ~clk; // 100MHz Clock

    // Test cases
    initial begin
        // Initialize inputs
        clk = 0;
        reset = 1;
        in = 0;

        // Apply reset
        #10;
        reset = 0;
        #10;
        reset = 1;
        #10;

        // Test Case 1: Normal flag sequence 01111110
        // Expect flag = 1, disc = 0, err = 0
        test_sequence(0);
        test_sequence(1);
        test_sequence(1);
        test_sequence(1);
        test_sequence(1);
        test_sequence(1);
        test_sequence(1);
        test_sequence(0);
        check_outputs(0, 1, 0);

        // Test Case 2: Discard sequence 0111110
        // Expect disc = 1, flag = 0, err = 0
        test_sequence(0);
        test_sequence(1);
        test_sequence(1);
        test_sequence(1);
        test_sequence(1);
        test_sequence(1);
        test_sequence(0);
        check_outputs(1, 0, 0);

        // Test Case 3: Error sequence 01111111
        // Expect err = 1, disc = 0, flag = 0
        test_sequence(0);
        test_sequence(1);
        test_sequence(1);
        test_sequence(1);
        test_sequence(1);
        test_sequence(1);
        test_sequence(1);
        test_sequence(1);
        check_outputs(0, 0, 1);

        // Resetting test environment
        #10;
        reset = 0;
        #10;

        $display("all test cases passed");
        $finish;
    end

    task test_sequence;
        input bit_value;
        begin
            in = bit_value;
            #10; // Wait one clock cycle
        end
    endtask

    task check_outputs;
        input expected_disc, expected_flag, expected_err;
        begin
            if (disc !== expected_disc || flag !== expected_flag || err !== expected_err) begin
                $display("Test failed: disc=%d flag=%d err=%d", disc, flag, err);
                $finish;
            end
        end
    endtask

endmodule

