module testbench;
    // inputs
    reg clk;
    reg reset;
    reg w;

    // output
    wire z;

    // Instantiate the Unit Under Test (UUT)
    top_module uut (
        .clk(clk),
        .reset(reset),
        .w(w),
        .z(z)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // Clock period of 10ns
    end

    // Initialize all inputs
    initial begin
        reset = 1;
        w = 0;

        // Reset the system
        #10 reset = 0;

        // Test sequence
        #10 check_state('h0, 0, 0); // A(0)-0->B
        #10 check_state('h0, 1, 0); // A(0)-1->A
        #10 check_state('h1, 0, 0); // B(0)-0->C
        #10 check_state('h1, 1, 0); // B(0)-1->D
        #10 check_state('h2, 0, 0); // C(0)-0->E
        #10 check_state('h2, 1, 0); // C(0)-1->D
        #10 check_state('h3, 0, 0); // D(0)-0->F
        #10 check_state('h3, 1, 0); // D(0)-1->A
        #10 check_state('h4, 0, 1); // E(1)-0->E
        #10 check_state('h4, 1, 0); // E(1)-1->D
        #10 check_state('h5, 0, 0); // F(1)-0->C
        #10 check_state('h5, 1, 0); // F(1)-1->D

        #10 if (test_passed == 6'h3F) $display("all test cases passed");

        #10 $finish;
    end

    reg [5:0] test_passed = 6'h00; // bit for each state test passed

    task check_state;
        input integer state;
        input integer next_w;
        input expected_z;
        begin
            w = next_w;
            @(posedge clk);  // Wait for the next clock edge
            if (z === expected_z) begin
                test_passed = test_passed | (1 << state);
            end
        end
    endtask
