module top_module_tb;
    reg clk, in, reset;
    wire out;

    top_module dut(
        .clk(clk),
        .in(in),
        .reset(reset),
        .out(out)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // Generate a clock with period 10ns
    end

    // Test Cases
    initial begin
        reset = 1; #10; // Apply reset
        reset = 0;

        // Check initial state (State A)
        // After reset, and `in` is 0 => should remain in State A with output 0
        in = 0;
        @(posedge clk); // Wait for the next clock edge
        if (out !== 0) fail_test();

        // State A, in=1 => Transition to State B
        in = 1;
        @(posedge clk);
        @(posedge clk); // Wait another cycle to stabilize the state
        if (out !== 0) fail_test();

        // State B, in=1 => Remain in State B
        in = 1;
        @(posedge clk);
        @(posedge clk); // Wait another cycle to stabilize the state
        if (out !== 0) fail_test();

        // State B, in=0 => Transition to State C
        in = 0;
        @(posedge clk);
        @(posedge clk); // Wait another cycle to stabilize the state
        if (out !== 0) fail_test();

        // State C, in=1 => Transition to State D
        in = 1;
        @(posedge clk);
        @(posedge clk); // Wait another cycle to stabilize the state
        if (out !== 1) fail_test();

        // State D, in=0 => Transition to State C
        in = 0;
        @(posedge clk);
        @(posedge clk); // Wait to verify output in State C
        if (out !== 0) fail_test();

        // State C, in=0 => Transition to State A
        in = 0;
        @(posedge clk);
        @(posedge clk); // Wait to verify output in State A
        if (out !== 0) fail_test();

        $display("all test cases passed");
        $finish;
    end

    task fail_test;
        begin
            $display("Test failed at time %t", $time);
            $finish;
        end
    endtask

endmodule

