module testbench;
    // Declare inputs as regs and outputs as wires
    reg clk, in, reset;
    wire done;

    // Instantiate the Device Under Test (DUT)
    top_module DUT (
        .clk(clk),
        .in(in),
        .reset(reset),
        .done(done)
    );

    // Clock process
    always begin
        #5 clk = ~clk;  // Clock toggles every 5ns
    end

    // Stimulus process
    initial begin
        // Initialize inputs
        clk = 0;  // Initial state of the clock
        reset = 1;  // Active-high reset
        in = 1;   // Line idle state
        #10;      // Wait for 10ns to sustain reset
        reset = 0;  // Release reset

        // Test case 1: correct frame, "011111110"
        stim_seq(1'b0, 8'hFF, 1'b1);

        // Test case 2: incorrect stop bit, "011111110"
        stim_seq(1'b0, 8'hFF, 1'b0);

        // Test case 3: correct frame, least significant bit first "000000001"
        stim_seq(1'b0, 8'h00, 1'b1);

        // Test Case 4: Line idle transitions without data "111111111"
        repeat (9) @ (posedge clk) in = 1'b1;

        // Test Case 5: Continuous correct frames
        stim_seq(1'b0, 8'hAA, 1'b1);  // 10101010
        stim_seq(1'b0, 8'h55, 1'b1);  // 01010101

        // Check if all tests passed
        #100; // wait for some time
        if (!$test$plusargs("error_detected")) begin
            $display("all test cases passed");
        end

        $finish;
    end

    // Utility task to simplify test vector inputs
    task stim_seq;
        input start_bit;
        input [7:0] data_bits;  // Least Significant Bit first
        input stop_bit;
    begin
        @ (posedge clk) in = start_bit;
        for (int i = 0; i < 8; i = i + 1) begin
            @ (posedge clk) in = data_bits[i];
        end
        @ (posedge clk) in = stop_bit;
        @ (posedge clk);  // Allow one more clock cycle for stop bit check
    end
    endtask

    // Monitor to detect errors
    always @(posedge done) begin
        if (in !== 1'b1) begin  // Check if the stop bit was incorrect
            $display("error: stop bit was not detected correctly after data transmission");
            $test$plusargs("error_detected");  // Set test plusarg on error
        end
    end

endmodule

