    reg reset;
    wire [23:0] out_bytes;
    wire done;

    // Instantiate the DUT
    top_module DUT(
        .clk(clk),
        .in(in),
        .reset(reset),
        .out_bytes(out_bytes),
        .done(done)
    );

    // Clock generation
    always #5 clk = ~clk;

    // Stimulus
    initial begin
        // Initialize signals
        clk = 0;
        in = 0;
        reset = 1;

        // Reset the DUT
        #10;
        reset = 0;
        #10;
        in = 8'h2C; // Not starting message
        #10;
        in = 8'h81; // Starts message
        #10;
        in = 8'h09;
        #10;
        in = 8'h6B; // Done should be asserted; out_bytes should be 2C8109
        #10;
        in = 8'h0D;
        #10;
        in = 8'h8D;
        #10;
        in = 8'h6D; // Done should be asserted; out_bytes should be 6B0D8D
        #10;
        in = 8'h12;
        #10;
        in = 8'h01;
        #10;
        in = 8'h0D; // Done should be asserted; out_bytes should be 6D1201
        #10;
        in = 8'h76; // Not starting message
        #10;
        in = 8'h3D; // Not starting message
        #10;
        in = 8'hED; // Done should be asserted; out_bytes should be D763D
        #10;
        in = 8'h8C;
        #10;
        in = 8'hF9;
        #10;
        in = 8'hCE; // Done should be asserted; out_bytes should be ED8CF9
        #10;
        finish_check();
    end

    // Check results and output pass/fail message
    reg test_failed;
    initial begin
        test_failed = 0;
        @(posedge done);
        // Confirm if the DUT outputs correct message
        if (out_bytes !== 24'h2C8109) test_failed = 1;
        @(posedge done);
        if (out_bytes !== 24'h6B0D8D) test_failed = 1;
        @(posedge done);
        if (out_bytes !== 24'h6D1201) test_failed = 1;
        @(posedge done);
        if (out_bytes !== 24'hD763D) test_failed = 1;
        @(posedge done);
        if (out_bytes !== 24'hED8CF9) test_failed = 1;
    end

    task finish_check;
        begin
            if (test_failed == 0) begin
                $display("all test cases passed");
            end
            $finish;
        end
    endtask
