module tb_top_module;

  // Inputs
  reg clk;
  reg load;
  reg [9:0] data;

  // Outputs
  wire tc;

  // Instantiate the Unit Under Test (UUT)
  top_module uut (
    .clk(clk), 
    .load(load), 
    .data(data), 
    .tc(tc)
  );

  // Clock generation
  always #5 clk = ~clk; // 100MHz Clock

  // Test variables
  integer test_cases_failed = 0;

  initial begin
    // Initialize Inputs
    clk = 0;
    load = 0;
    data = 0;

    // Wait for global reset
    #100;

    // Test Case 1: Loading and counting to zero
    load = 1; data = 10'd10;  // Load counter with 10
    #10;
    load = 0;                // Start counting down
    repeat (10) #10;
    if (tc !== 1'b1) begin
      test_cases_failed = test_cases_failed + 1;
      $display("Test Case 1 Failed: tc should be high when reached 0");
    end

    // Test Case 2: Counter should stay at zero when reached zero
    repeat (5) #10;
    if (tc !== 1'b1) begin
      test_cases_failed = test_cases_failed + 1;
      $display("Test Case 2 Failed: tc should remain high if no re-load");
    end

    // Test Case 3: Re-load during counting
    load = 1; data = 10'd5;  // Re-load counter with 5 during countdown
    #10;
    load = 0;
    repeat (5) #10;
    if (tc !== 1'b1) begin
      test_cases_failed = test_cases_failed + 1;
      $display("Test Case 3 Failed: tc should be high after re-load and count down");
    end

    // Test Case 4: Load again after count has completed 
    load = 1; data = 10'd8;
    #10;
    load = 0;
    repeat (8) #10;
    if (tc !== 1'b1) begin
      test_cases_failed = test_cases_failed + 1;
      $display("Test Case 4 Failed: tc should be high when re-loaded and counted to zero again");
    end

    // Check and display test results
    if (test_cases_failed == 0) begin
      $display("all test cases passed");
    end

    #10 $finish;
  end

endmodule

