module testbench;
    reg in; 
    reg [3:0] state;
    wire [3:0] next_state;
    wire out;
    integer i;

    // Instance of the DUT
    top_module dut (
        .in(in),
        .state(state),
        .next_state(next_state),
        .out(out)
    );

    initial begin
        // Initialize all inputs
        in = 0;
        state = 4'b0001;  // Start at state A

        // Test for state A with input combinations
        #10;
        in = 0; 
        if (next_state !== 4'b0001 && out !== 0) begin
            $display("Test failed for state A with in = 0");
        end
        #10;
        in = 1;
        if (next_state !== 4'b0010 && out !== 0) begin
            $display("Test failed for state A with in = 1");
        end

        // Move to state B
        #10;
        state = 4'b0010;
        in = 0;
        if (next_state !== 4'b0100 && out !== 0) begin
            $display("Test failed for state B with in = 0");
        end
        #10;
        in = 1;
        if (next_state !== 4'b0010 && out !== 0) begin
            $display("Test failed for state B with in = 1");
        end

        // Move to state C
        #10;
        state = 4'b0100;
        in = 0;
        if (next_state !== 4'b0001 && out !== 0) begin
            $display("Test failed for state C with in = 0");
        end
        #10;
        in = 1;
        if (next_state !== 4'b1000 && out !== 0) begin
            $display("Test failed for state C with in = 1");
        end

        // Move to state D
        #10;
        state = 4'b1000;
        in = 0;
        if (next_state !== 4'b0100 && out !== 1) begin
            $display("Test failed for state D with in = 0");
        end
        #10;
        in = 1;
        if (next_state !== 4'b0010 && out !== 1) begin
            $display("Test failed for state D with in = 1");
        end

        // Test all combinations and ensure all tests pass
        #10;
        for (i = 0; i <= 15; i = i + 1) begin
            state = i;
            in = 0;
            #1;
            if (!((state == 4'b0001 && next_state == 4'b0001 && out == 0) ||
                  (state == 4'b0010 && next_state == 4'b0100 && out == 0) ||
                  (state == 4'b0100 && next_state == 4'b0001 && out == 0) ||
                  (state == 4'b1000 && next_state == 4'b0100 && out == 1))) begin
                $display("General test failed for state %b with in = 0", state);
                $finish;
            end
            in = 1;
            #1;
            if (!((state == 4'b0001 && next_state == 4'b0010 && out == 0) ||
                  (state == 4'b0010 && next_state == 4'b0010 && out == 0) ||
                  (state == 4'b0100 && next_state == 4'b1000 && out == 0) ||
                  (state == 4'b1000 && next_state == 4'b0010 && out == 1))) begin
                $display("General test failed for state %b with in = 1", state);
                $finish;
            end
        end

        $display("all test cases passed");
    end
endmodule

