module testbench;
    reg clk, in, reset;
    wire [7:0] out_byte;
    wire done;

    // Instance of the DUT
    top_module DUT (
        .clk(clk),
        .in(in),
        .reset(reset),
        .out_byte(out_byte),
        .done(done)
    );

    // Clock generation
    always #5 clk = ~clk;

    // Replacing struct with separate arrays
    reg [9:0] input_bits[4];
    reg correct_stop[4];

    // Test vectors
    integer i, error_count;
    reg [7:0] expected_data;
    reg expected_done;

    initial begin
        clk = 0;
        in = 1; // Idle line state
        reset = 1;
        #10 reset = 0;
        #10 reset = 1; // Active high reset, then deassert

        // Define test vectors
        input_bits[0] = 10'b0_01101001_1; correct_stop[0] = 1'b1; // Correct sequence
        input_bits[1] = 10'b0_10100111_1; correct_stop[1] = 1'b1; // Correct sequence
        input_bits[2] = 10'b0_11000011_1; correct_stop[2] = 1'b1; // Correct sequence
        input_bits[3] = 10'b0_11110000_0; correct_stop[3] = 1'b1; // Incorrect sequence

        error_count = 0;

        // Run test vectors
        for (i = 0; i < 4; i = i + 1) begin
            expected_data = input_bits[i][8:1]; // Extract data bits
            expected_done = correct_stop[i];    // Stop bit used to determine done

            // Apply test bits
            for (integer j = 9; j >= 0; j = j - 1) begin
                in = input_bits[i][j];
                #10; // Wait for 1 bit-time
            end

            in = 1; // Return to idle (stop bit)
            #20; // Wait for processing

            // Check results
            if (done !== expected_done || (done && out_byte !== expected_data)) begin
                $display("Test case %d failed: Expected done = %b and out_byte = 0x%02h, got done = %b and out_byte = 0x%02h", i, expected_done, expected_data, done, out_byte);
                error_count = error_count + 1;
            end
        end

        if (error_count == 0) begin
            $display("all test cases passed");
        end

        $finish;
    end
endmodule

