        // Initialize Inputs
        areset = 1; // Asynchronous reset
        in = 0;

        // Wait for the clock to ensure proper async reset
        @(posedge clk);
        #1 areset = 0; // Release reset

        // Test Case 1: Stay in State A with in=0
        @(posedge clk);
