        @(posedge clk);
        if (out !== 0) $display("Test Case 4 Failed: B to C with in=0");

        // Test Case 5: Transition to State A with in=0
        @(posedge clk);
        if (out !== 0) $display("Test Case 5 Failed: C to A with in=0");

        // Test Case 6: Transition to State B with in=1
        in = 1;
        @(posedge clk);
        if (out !== 1) $display("Test Case 6 Failed: A to B with in=1");

