module testbench;

  // Inputs
  reg [2:0] a;
  reg [2:0] b;

  // Outputs
  wire [2:0] out_or_bitwise;
  wire out_or_logical;
  wire [5:0] out_not;

  // Instantiate the Device Under Test (DUT)
  top_module dut (
    .a(a), 
    .b(b), 
    .out_or_bitwise(out_or_bitwise),
    .out_or_logical(out_or_logical),
    .out_not(out_not)
  );

  // Test variables
  integer i, errors;

  initial begin
    // Initialize Inputs
    a = 0;
    b = 0;
    errors = 0;

    // Wait for global reset
    #100;

    // Test Cases
    for (i = 0; i < 8; i++) begin
      for (int j = 0; j < 8; j++) begin
        a = i; b = j;
        #10; // wait for the outputs to settle

        // Check outputs
        if (out_or_bitwise !== (a | b)) begin
          $display("Test failed for a: %b, b: %b: out_or_bitwise expected: %b, received: %b", a, b, (a | b), out_or_bitwise);
          errors = errors + 1;
        end
        if (out_or_logical !== (| (a | b))) begin
          $display("Test failed for a: %b, b: %b: out_or_logical expected: %b, received: %b", a, b, (| (a | b)), out_or_logical);
          errors = errors + 1;
        end
        if (out_not[5:3] !== ~b) begin
          $display("Test failed for b: %b: upper out_not expected: %b, received: %b", b, ~b, out_not[5:3]);
          errors = errors + 1;
        end
        if (out_not[2:0] !== ~a) begin
          $display("Test failed for a: %b: lower out_not expected: %b, received: %b", a, ~a, out_not[2:0]);
          errors = errors + 1;
        end
      end
    end

    // Check for overall success
    if (errors == 0) begin
      $display("all test cases passed");
    end

    $finish;

  end
endmodule

