    initial begin
        // Initialize test vectors and expected results
        // Index:   d c b a - Binary Representation
        test_vectors[0]  = 4'b0000; expected_sop[0] = 0; expected_pos[0] = 0;
        test_vectors[1]  = 4'b0001; expected_sop[1] = 0; expected_pos[1] = 0;
        test_vectors[2]  = 4'b0010; expected_sop[2] = 1; expected_pos[2] = 1;
        test_vectors[3]  = 4'b0100; expected_sop[3] = 0; expected_pos[3] = 0;
        test_vectors[4]  = 4'b0101; expected_sop[4] = 0; expected_pos[4] = 0;
        test_vectors[5]  = 4'b0110; expected_sop[5] = 0; expected_pos[5] = 0;
        test_vectors[6]  = 4'b0111; expected_sop[6] = 1; expected_pos[6] = 1;
        test_vectors[7]  = 4'b1001; expected_sop[7] = 0; expected_pos[7] = 0;
