module testbench;
    // Testbench signals
    reg sel;
    reg [7:0] a;
    reg [7:0] b;
    wire [7:0] out;

    // Instantiate the Device Under Test (DUT)
    top_module dut (
        .sel(sel),
        .a(a),
        .b(b),
        .out(out)
    );

    // Test cases counter and flag
    integer errors = 0;

    // Task to perform each test case
    task perform_test;
        input reg sel_test;
        input reg [7:0] a_test;
        input reg [7:0] b_test;
        input reg [7:0] expected_out;
        begin
            sel = sel_test;
            a = a_test;
            b = b_test;
            #10; // Wait for values to propagate

            if (out !== expected_out) begin
                $display("Test failed: sel=%b, a=%h, b=%h, Expected out=%h, Received out=%h",
                         sel_test, a_test, b_test, expected_out, out);
                errors = errors + 1;
            end
        end
    endtask

    // Initial block for tests
    initial begin
        // Test case 1
        perform_test(0, 8'hAA, 8'h55, 8'hAA);
        // Test case 2
        perform_test(1, 8'hAA, 8'h55, 8'h55);
        // Test case 3
        perform_test(0, 8'h00, 8'hFF, 8'h00);
        // Test case 4
        perform_test(1, 8'h00, 8'hFF, 8'hFF);
        // Test case 5
        perform_test(1, 8'hFF, 8'h00, 8'h00);
        // Test case 6
        perform_test(0, 8'h13, 8'h89, 8'h13);

        #10; // Allow time for final propagation

        if (errors == 0) begin
            $display("All test cases passed");
        end
        $finish;
    end

endmodule

