            $display("Failure: Expected output to stay 1 in ON state when j=0 and k=0.");
            fail_count = fail_count + 1;
        end

        // Test transitioning back to OFF with k=1
        k = 1; #10;
        if (out !== 0) begin
            $display("Failure: Expected output to be 0 when transitioning back to OFF state.");
            fail_count = fail_count + 1;
        end

        // Test staying in OFF with j=0
        j = 0; k = 0; #20;
        if (out !== 0) begin
            $display("Failure: Expected output to stay 0 in OFF state when j=0.");
            fail_count = fail_count + 1;
        end

        // Check if all test cases passed
        if (fail_count == 0) $display("all test cases passed");

        // Finish the simulation
        $finish;
    end

