module testbench;
    reg clk;
    reg areset;
    reg predict_valid;
    reg predict_taken;
    wire [31:0] predict_history;
    reg train_mispredicted;
    reg train_taken;
    reg [31:0] train_history;

    top_module DUT (
        .clk(clk),
        .areset(areset),
        .predict_valid(predict_valid),
        .predict_taken(predict_taken),
        .predict_history(predict_history),
        .train_mispredicted(train_mispredicted),
        .train_taken(train_taken),
        .train_history(train_history)
    );

    // Clock generation
    always #5 clk = ~clk; // Clock period of 10ns

    // Stimulus
    initial begin
        clk = 0;
        areset = 1;
        predict_valid = 0;
        predict_taken = 0;
        train_mispredicted = 0;
        train_taken = 0;
        train_history = 0;

        // Asynchronous reset 
        #10;
        areset = 0;
        #10;
        areset = 1;
        #10;
        areset = 0;

        // Case 1: No prediction or misprediction
        wait_for_next_cycle();

        // Case 2: Single prediction
        predict_valid = 1;
        predict_taken = 1;
        wait_for_next_cycle();
        check_history(32'b0000_0000_0000_0000_0000_0000_0000_0001);

        // Case 3: Another prediction
        predict_valid = 1;
        predict_taken = 0;
        wait_for_next_cycle();
        check_history(32'b0000_0000_0000_0000_0000_0000_0000_0010);

        // Case 4: Train misprediction corrects the history
        train_mispredicted = 1;
        train_taken = 1;
        train_history = 32'b1010;
        wait_for_next_cycle();
        check_history(32'b1011);

        // Case 5: Simultaneous prediction and misprediction, misprediction takes precedence
        predict_valid = 1;
        predict_taken = 1;
        train_mispredicted = 1;
        train_taken = 0;
        train_history = 32'hffff_ffff;
        wait_for_next_cycle();
        check_history(32'hffff_fffe);

        // Check all test cases passed
        if(test_passed) begin
            $display("all test cases passed");
        end
        $finish;
    end

    task wait_for_next_cycle;
    begin
        #10; // Wait for next positive clock edge
        predict_valid = 0;
        train_mispredicted = 0;
    end
    endtask

    reg test_passed = 1;
    task check_history;
        input [31:0] expected_history;
    begin
        if(predict_history !== expected_history) begin
            test_passed = 0;
            $display("Test failed, Expected history: %b, Got: %b", expected_history, predict_history);
        end
    end
    endtask

endmodule

