        clk = 0;
        forever #5 clk = ~clk; // Generate a clock with period of 10ns (100MHz)
    end

    // Test cases
    initial begin
        // Initialize Input
        reset = 1'b1; in = 1'b1;  // Start with reset and line idle
        #10;  // Wait for the reset to take effect
        reset = 1'b0;

        // Test Case 1: Correct byte reception
        in = 0; #10;  // Start bit (0)
        in = 1; #10;  // bit0 (LSB)
        in = 0; #10;  // bit1
        in = 1; #10;  // bit2
        in = 0; #10;  // bit3
        in = 1; #10;  // bit4
        in = 1; #10;  // bit5
        in = 0; #10;  // bit6
        in = 1; #10;  // bit7
        in = 1; #10;  // Stop bit (1)
        if (!done) $display("Test Case 1 failed.");

        // Test Case 2: Incorrect stop bit
        in = 0; #10;  // Start bit (0)
        in = 0; #10;  // bit0 (LSB)
        in = 0; #10;  // bit1
        in = 0; #10;  // bit2
        in = 0; #10;  // bit3
        in = 0; #10;  // bit4
        in = 0; #10;  // bit5
        in = 0; #10;  // bit6
        in = 0; #10;  // bit7
        in = 0; #10;  // Stop bit (0), should be 1
        if (done) $display("Test Case 2 failed.");

        // Reset to seek new valid data
        reset = 1'b1; #10;
        reset = 1'b0;
        in = 1; #90;  // Idle state until a start bit is detected

        // Test Case 3: Back to correct reception after error
        in = 0; #10;  // Start bit
        in = 1; #10;  // bit0 (LSB)
        in = 1; #10;  // bit1
        in = 1; #10;  // bit2
        in = 1; #10;  // bit3
        in = 1; #10;  // bit4
        in = 1; #10;  // bit5
        in = 1; #10;  // bit6
        in = 1; #10;  // bit7
        in = 1; #10;  // Stop bit (1)
        if (!done) $display("Test Case 3 failed.");

        // Check all test cases
        #10;
        if (done) $display("all test cases passed");

