module testbench;

    // Inputs
    reg in;
    reg [3:0] state;

    // Outputs
    wire [3:0] next_state;
    wire out;

    // Instantiate the Device Under Test (DUT)
    top_module dut (
        .in(in),
        .state(state),
        .next_state(next_state),
        .out(out)
    );

    // Variables
    reg [3:0] expected_next_state;
    reg expected_out;
    integer cases_passed, total_cases;
    integer i;

    // Test case procedure
    initial begin
        // Initialize inputs and variables
        in = 0; 
        state = 4'b0001; // Start in state A
        cases_passed = 0;
        total_cases = 8; // Total different test cases

        // Display header
        $display("State (in binary), Input, Expected Next State, Next State, Expected Output, Output");

        // Checking each state transition and output
        for(i = 0; i < total_cases; i = i + 1) begin
            case(state)
                4'b0001: begin // State A
                    if(in == 0) begin
                        expected_next_state = 4'b0001; // A -> A
                        expected_out = 0;
                    end else begin
                        expected_next_state = 4'b0010; // A -> B
                        expected_out = 0;
                    end
                end
                4'b0010: begin // State B
                    if (in == 0) begin
                        expected_next_state = 4'b0100; // B -> C
                        expected_out = 0;
                    end else begin
                        expected_next_state = 4'b0010; // B -> B
                        expected_out = 0;
                    end
                end
                4'b0100: begin // State C
                    if (in == 0) begin
                        expected_next_state = 4'b0001; // C -> A
                        expected_out = 0;
                    end else begin
                        expected_next_state = 4'b1000; // C -> D
                        expected_out = 0;
                    end
                end
                4'b1000: begin // State D
                    if (in == 0) begin
                        expected_next_state = 4'b0100; // D -> C
                        expected_out = 1;
                    end else begin
                        expected_next_state = 4'b0010; // D -> B
                        expected_out = 1;
                    end
                end
            endcase

            #10; // Delay for state settling

            //Test printout
            $display("%b, %b, %b, %b, %d, %d", state, in, expected_next_state, next_state, expected_out, out);

            // Check if test passed
            if (next_state === expected_next_state && out === expected_out) begin
                cases_passed = cases_passed + 1;
            end

            // Next test case setup
            state = next_state; // Move to next state
            in = ~in; // Toggle input
        end

        // Final check if all tests passed
        if (cases_passed == total_cases) begin
            $display("all test cases passed");
        end

        // Finish the test
        $finish;
    end

endmodule

