    initial begin
        clk = 0;
        forever #5 clk = ~clk;
    end

    // Test cases
    initial begin
        // Initialize inputs
        areset = 1;
        x = 0;
        #10;

        // Test case 1: Input = 011 (3 in decimal) -> Output should be 101 (2's complement of 3)
        areset = 0;
        send_bits('b011);

        // Test case 2: Input = 0010 (2 in decimal) -> Output should be 1110 (2's complement of 2)
        send_bits('b0010);

        // Additional tests can be added here following the same pattern

        // Check all test cases
        if(testing_success)
            $display("all test cases passed");
        else
            $display();

        $finish;
    end

    // Procedure to send bits to the DUT
    task send_bits;
        input [31:0] data;
        integer i;
        begin
            for (i = 0; i < 32 && data[i] !== 1'bx; i = i + 1) begin
                x = data[i];
                #10;
            end
            areset = 1; // terminate the input by asserting reset
            #10;
            areset = 0; // de-assert reset for the next test case
        end
    endtask

    // Test success checker
    reg [31:0] expected_outputs [0:100];
    reg [4:0] expected_index = 0;
    reg testing_success = 1;

    initial begin
        // Expected output for each corresponding input
        expected_outputs[0] = 'b101; // 2's comp of 011
        expected_outputs[1] = 'b1110; // 2's comp of 0010
        expected_index = 0;
    end

    always @(posedge clk) begin
        if (areset === 0 && x !== 1'bx && expected_outputs[expected_index] !== z) begin
            testing_success = 0;
        end
        if (x === 1'bx) begin
            expected_index = expected_index + 1; // Move to the next expected output
        end
    end
