        .reset(reset),
        .done(done)
    );

    // Clock generation
    always #5 clk = !clk;  // Generate a clock with period 10ns

    // Test cases
    initial begin
        // Initialize
        clk = 0;
        reset = 1;
        in = 0;
        #10;  // Reset period

        reset = 0;

        // Stimulate the DUT - Test case 1: Proper message boundaries detected
        in = 8'b10000000; // Start byte: in[3]=1
        #10;
        in = 8'b00110011; // Middle byte
        #10;
        in = 8'b01010101; // End byte
        #10;
        if (!done) 
            $display("Test case 1 failed. Expected done=1");

        // Test case 2: Delay until valid start is found
        in = 8'b01110000; // invalid
        #10;
        in = 8'b01100000; // invalid
        #10;
        in = 8'b10010101; // Start byte: correct start
        #10;
        in = 8'b00000011; // Continuation
        #10;
        in = 8'b11110000; // End of message
        #10;
        if (!done)
            $display("Test case 2 failed. Expected done=1");

        // Test case 3: Reset in middle of a message
        in = 8'b10010010; // Start byte: looks valid
        #10;
        reset = 1;        // issue a reset
        #10;
        reset = 0;
        in = 8'b00001111; // Should be ignored
        #10;
        in = 8'b10000000; // New valid start
        #10;
        in = 8'b00111100; // Middle
        #10;
        in = 8'b00000001; // Message completes
        #10;
        if (!done)
            $display("Test case 3 failed. Expected done=1");

        // Check if all test cases passed
        #10;
        // Since $display in case of failure occurs in an earlier statement, 
        // this will only display if all are passed.
        if (!($test$plusargs("Test case 1 failed") || $test$plusargs("Test case 2 failed") || $test$plusargs("Test case 3 failed")))
