module testbench;
    reg clk, j, k;
    reg expected_Q;
    wire Q;
    top_module dut (clk, j, k, Q);

    initial begin
        clk = 0;
        forever #5 clk = ~clk; // Generate a clock with 10 ns period
    end

    initial begin
        // Initializing inputs
        j = 0; k = 0;
        @(posedge clk); // Wait for the first positive edge of the clock
        expected_Q = Q; // Initial state of Q

        // Test Case 1: J = 0, K = 0 --> Q should remain same
        @(posedge clk); // Wait for clock edge
        if (Q !== expected_Q) $display("Test case failed: J=0, K=0");

        // Test Case 2: J = 0, K = 1 --> Q should be 0
        j = 0; k = 1;
        @(posedge clk); // Wait for clock edge
        expected_Q = 0;
        if (Q !== expected_Q) $display("Test case failed: J=0, K=1");

        // Test Case 3: J = 1, K = 0 --> Q should be 1
        j = 1; k = 0;
        @(posedge clk); // Wait for clock edge
        expected_Q = 1;
        if (Q !== expected_Q) $display("Test case failed: J=1, K=0");

        // Test Case 4: J = 1, K = 1 --> Q should toggle
        j = 1; k = 1;
        @(posedge clk); // Wait for clock edge
        expected_Q = ~Q;
        if (Q !== expected_Q) $display("Test case failed: J=1, K=1");

        $finish; // Correctly terminate simulation
    end

    initial begin
        #1000; // Assuming simulation time sufficient for all test cases
        if (!$isunknown(Q) && !$test$plusargs("error")) $display("all test cases passed");
    end
endmodule

