module testbench;
    reg clk, reset, ena;
    wire pm;
    wire [7:0] hh, mm, ss;

    top_module dut(
        .clk(clk),
        .reset(reset),
        .ena(ena),
        .pm(pm),
        .hh(hh),
        .mm(mm),
        .ss(ss)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // Assume clk period is 10ns for fast-run simulation
    end

    // Test scenarios
    initial begin
        reset = 1; ena = 0;
        #10; // wait for resetting

        reset = 0; // release reset
        // Verify reset condition
        if (hh !== 8'b00000000 || mm !== 8'b00000000 || ss !== 8'b00000000 || pm !== 1'b0) begin
            $finish;
        end

        // Test case: increment seconds
        ena = 1;
        repeat (59) begin
            #10;
            if (ss == 8'b00111011) begin // Check 59 seconds
                ena = 0;
                #10; ena = 1; // Increment to observe rollover
                #10; // to switch to the next minute
                if (mm !== 8'b00000001 || ss !== 8'b00000000) $finish; // Failure on minute or second rollover
            end
        end

        // Test case: increment minutes and hours, observe AM/PM toggle
        repeat(59) begin // Increment minutes until hours should increment
            repeat(59) #10; // increment seconds full cycle
            #10;
            if (mm !== 8'b00000000 || (hh[7:4] * 10 + hh[3:0]) % 12 != 1) $finish; // Check hour increment and reset of minutes
        end

        #10; // Increment seconds to roll hour
        repeat(59) #10;
        #10; // Now hour should be 12 and toggle AM/PM
        if (pm !== 1'b1) $finish; // verify PM flag

        // Run through PM cycle to ensure PM reset on 12:00 wraparound
        ena = 0; reset = 1; // Reset to start AM again
        #10; reset = 0; ena = 1;
        repeat(12 * 60 * 60) #10; // Simulate 12 hours
        if (pm !== 1'b0 || hh !== 8'b00010000) $finish; // Failure to wraparound to 12:00 AM

        // If all tests pass
        $display("all test cases passed");

        $finish;
    end
