        ack = 0;
        state = 10'b0000000001; // S state

        // Reset condition
        #10;
        state = 10'b0000000001; // S state
        assert(S_next === 1'b1) else $display("Test case failed: S_next logic");

        // Transition from S to S1
        #10 d = 1;
        #10 assert(S1_next === 1'b1) else $display("Test case failed: S to S1 transition");

        // Cross checking the output 'shift_ena' in states B0-B3
        #10 state = 10'b0000100000; // B0 state
        #10 assert(shift_ena === 1'b1) else $display("Test case failed: shift_ena in B0 state");

        #10 state = 10'b0001000000; // B1 state
        #10 assert(shift_ena === 1'b1) else $display("Test case failed: shift_ena in B1 state");

        #10 state = 10'b0010000000; // B2 state
        #10 assert(shift_ena === 1'b1) else $display("Test case failed: shift_ena in B2 state");

        #10 state = 10'b0100000000; // B3 state
        #10 assert(shift_ena === 1'b1) else $display("Test case failed: shift_ena in B3 state");
        #10 assert(B3_next === 1'b1) else $display("Test case failed: B3_next logic");

        // Transition to 'Count' state and testing 'counting' output
        #10 state = 10'b0100000000; // B3 state
        #10 assert(counting === 1'b1) else $display("Test case failed: counting in Count state");

        // Check for done scenario in 'Wait' state
        #10 state = 10'b1000000000; // Wait state
        done_counting = 1;
        ack = 1;
        #10 assert(done === 1'b1) else $display("Test case failed: done output in Wait state");
        #10 assert(Wait_next === 1'b1) else $display("Test case failed: Wait_next logic");

        // Return to 'S' state from 'Wait'
        #10 state = 10'b1000000000; // Wait state
        ack = 1;
        #10 assert(S_next === 1'b1) else $display("Test case failed: Return to S from Wait");

        // All cases pass
        $display("all test cases passed");
    end

endmodule

