module testbench;
    reg clk, reset, in;
    wire [7:0] out_byte;
    wire done;

    top_module dut(
        .clk(clk),
        .in(in),
        .reset(reset),
        .out_byte(out_byte),
        .done(done)
    );

    initial begin
        clk = 0;
        forever #5 clk = ~clk; // 100MHz clock
    end

    initial begin
        $monitor("Time=%g, in=%b, done=%d, out_byte=%h", $time, in, done, out_byte);

        reset = 1; // Asserting reset
        #10;
        reset = 0; // Deassert reset

        // Test Case 1: Correct transmission 0x55
        send_byte(8'b01101010); // LSB first, start (0) and stop (1) bits included
        check_byte(8'h55);

        // Test Case 2: Incorrect stop bit, followed by correct transmission 0xAA
        send_byte(8'b010101010); // Incorrect stop bit 0
        send_byte(8'b101010101); // Correct byte 0xAA
        check_byte(8'hAA);

        // Test Case 3: Immediate correct byte after incorrect one
        send_byte(8'b011111110); // Incorrect stop
        send_byte(8'b100110001); // Correct byte 0x99
        check_byte(8'h99);

        // More test cases can be added similarly...

        #50;
        if ($test$plusargs("all_cases_passed")) begin
            $display("all test cases passed");
        end

        $finish;
    end

    task send_byte;
        input [8:0] data; // Includes the start and stop bits
        integer i;
        begin
            for (i = 0; i < 9; i = i + 1) begin
                in = data[i];
                #10; // Bit period
            end
            in = 1; // Return to idle
            #20; // Allow detection of stop bit and start of idle
        end
    endtask

    task check_byte;
        input [7:0] expected_byte;
        begin
            wait(done == 1);
            if (out_byte !== expected_byte) begin
                $display("Test failed at time %t, expected 0x%h, received 0x%h", $time, expected_byte, out_byte);
            end
            @(posedge clk); // wait for one clock cycle after checking byte
        end
    endtask

endmodule

