module testbench;
    reg clk, areset, bump_left, bump_right;
    wire walk_left, walk_right;

    top_module dut(
        .clk(clk),
        .areset(areset),
        .bump_left(bump_left),
        .bump_right(bump_right),
        .walk_left(walk_left),
        .walk_right(walk_right)
    );

    initial begin
        clk = 0;
        forever #5 clk = ~clk; // Toggle clock every 5 ns
    end

    task reset;
        begin
            areset = 1;
            @(posedge clk);
            #1;
            areset = 0;
        end
    endtask

    task check_state(input expected_walk_left, expected_walk_right);
        begin
            if (!(walk_left === expected_walk_left && walk_right === expected_walk_right)) begin
                $display("Test case failed: Expected walk_left = %b, walk_right = %b; Received walk_left = %b, walk_right = %b", 
                         expected_walk_left, expected_walk_right, walk_left, walk_right);
                $finish;
            end
        end
    endtask

    initial begin
        reset();

        // Test 1: Default state after reset (Should walk left)
        @(negedge clk);
        check_state(1, 0);

        // Test 2: Bump on left -> Should start walking right
        bump_left = 1;
        @(negedge clk);
        check_state(0, 1);
        bump_left = 0; // Remove bump

        // Test 3: Bump on right -> Should start walking left
        bump_right = 1;
        @(negedge clk);
        check_state(1, 0);
        bump_right = 0; // Remove bump

        // Test 4: Bump on both sides -> Should toggle to walking right
        bump_left = 1;
        bump_right = 1;
        @(negedge clk);
        check_state(0, 1);
        bump_left = 0;
        bump_right = 0; // Remove both bumps

        // Test 5: No bump -> Continue walking right
        @(negedge clk);
        check_state(0, 1);

        $display("all test cases passed");
        $finish;
    end
endmodule

