        clk = 0;
        forever #5 clk = ~clk; // 100 MHz clock (10 ns period)
    end

    // Test cases
    integer i;
    integer error_count = 0;

    initial begin
        reset = 0; // reset is not active
        @(posedge clk);
        reset = 1; // assert reset on rising edge
        @(posedge clk);
        reset = 0; // de-assert reset

        // Check reset functionality (should reset to 1)
        if (q !== 4'b0001) begin
            $display("Reset test failed, q = %d", q);
            error_count = error_count + 1;
