            @(posedge clk); // wait for the next rising edge of clock
            // should count from 1 to 10
            if (q !== i[3:0]) begin
                $display("Counting test failed at i = %d, q = %d", i, q);
                error_count = error_count + 1;
            end
        end

        // Extra check to make sure it wraps around to 1 after 10
        @(posedge clk);
        if (q !== 1) begin
            $display("Wrap around test failed, q = %d", q);
            error_count = error_count + 1;
        end

        // Assess all tests pass condition
        if (error_count == 0) begin
            $display("all test cases passed");
