module Testbench;
    reg clk;
    reg reset;
    reg s;
    reg [1:0] w; // Adjusted the size to avoid using unpacked arrays with tasks
    wire z;

    top_module dut(
        .clk(clk),
        .reset(reset),
        .s(s),
        .w(w),
        .z(z)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // Clock period is 10ns
    end

    // Declare a task to handle the reset
    task perform_reset;
        begin
            reset = 1;
            repeat (2) @(posedge clk);
            reset = 0;
        end
    endtask

    // Test cases evaluation
    integer errors = 0;

    // Test case procedure
    task test_case;
        input [1:0] w_sequence0;
        input [1:0] w_sequence1;
        input [1:0] w_sequence2;
        input integer expected_z;
        integer i;
        begin
            perform_reset();
            s = 1;
            @(posedge clk);
            w = w_sequence0;
            @(posedge clk);
            w = w_sequence1;
            @(posedge clk);
            w = w_sequence2;
            @(posedge clk);
            if (z !== expected_z) begin
                $display("Test failed with w_sequence {%b, %b, %b}, expected %d, got %d", w_sequence0, w_sequence1, w_sequence2, expected_z, z);
                errors = errors + 1;
            end
        end
    endtask

    // Run test scenarios
    initial begin
        // Wait for reset to be de-asserted
        perform_reset();

        // Test scenarios
        test_case(2'b00, 2'b01, 2'b01, 1);
        test_case(2'b01, 2'b00, 2'b01, 1);
        test_case(2'b01, 2'b01, 2'b00, 1);
        test_case(2'b01, 2'b01, 2'b01, 0);
        test_case(2'b00, 2'b00, 2'b00, 0);
        test_case(2'b00, 2'b00, 2'b01, 0);
        test_case(2'b00, 2'b01, 2'b00, 0);

        // Check all test cases
        if (errors == 0) begin
            $display("all test cases passed");
        end

        // Finish simulation
        $finish;
    end
endmodule

