    reg clk;
    reg areset;
    reg train_valid;
    reg train_taken;
    wire [1:0] state;
    reg test_failed = 0; // Flag to check if any test has failed

    top_module dut (
        .clk(clk),
        .areset(areset),
        .train_valid(train_valid),
        .train_taken(train_taken),
        .state(state)
    );

    // Generate clock
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // Clock with a period of 10ns
    end

    // Stimulus
    initial begin
        // Test Initialization by Asynch Reset
        areset = 1; #10;
        areset = 0;
        if (state !== 2'b01) begin
            $display("Reset test failed");
            test_failed = 1;
        end

        // Test counter incrementation
        train_valid = 1;
        train_taken = 1;
        #10; // wait one cycle
        if (state !== 2'b10) begin
            $display("Increment test failed 01 to 10");
            test_failed = 1;
        end

        #10; // wait one cycle
        if (state !== 2'b11) begin
            $display("Increment test failed 10 to 11");
            test_failed = 1;
        end

        #10; // wait one cycle
        if (state !== 2'b11) begin
            $display("Saturating test failed at 11");
            test_failed = 1;
        end

        // Test counter decrementation
        train_taken = 0;
        #10; // wait one cycle
        if (state !== 2'b10) begin
            $display("Decrement test failed from 11 to 10");
            test_failed = 1;
        end

        #10; 
        if (state !== 2'b01) begin
            $display("Decrement test failed from 10 to 01");
            test_failed = 1;
        end

        #10; 
        if (state !== 2'b00) begin
            $display("Decrement test failed from 01 to 00");
            test_failed = 1;
        end

        #10; 
        if (state !== 2'b00) begin
            $display("Saturating test failed at 00");
            test_failed = 1;
        end

        // Test no change given train_valid = 0
        train_valid = 0;
        train_taken = 1; // This change should have no effect
        #20; 
        if (state !== 2'b00) begin
            $display("No change test failed when valid is 0");
            test_failed = 1;
        end

