module testbench;
    reg clk, reset, data, done_counting, ack;
    wire shift_ena, counting, done;

    top_module dut(
        .clk(clk),
        .reset(reset),
        .data(data),
        .shift_ena(shift_ena),
        .counting(counting),
        .done_counting(done_counting),
        .done(done),
        .ack(ack)
    );

    initial begin
        clk = 0;
        reset = 1;  // reset the system
        data = 0;
        done_counting = 0;
        ack = 0;

        // Take the system out of reset
        #10 reset = 0;

        // Create clock
        forever #5 clk = !clk;
    end

    initial begin
        // Wait for reset to be over
        #20;

        // Test pattern detection
        send_bits(4'b1101); // Send start pattern
        #40;
        test_shift_ena();

        // Start counting after pattern and 4-bit delay input
        send_bits(4'b1010);
        #40;
        test_counting();

        // Test done condition
        done_counting = 1;
        #10;
        test_done();

        // Test ack reception
        ack = 1;
        #10;
        test_ack();

        // Check if everything passed
        check_results();
    end

    task send_bits;
        input [3:0] bits;
        integer i;
        begin
            for (i = 3; i >= 0; i = i - 1) begin
                data = bits[i];
                #10; // Shift in the next bit at every clock
            end
        end
    endtask

    reg [4:0] test_cases = 5'b11111; // Track passing of tests, if all are 1 at end, all tests passed

    task test_shift_ena;
        begin
            if (shift_ena !== 1) test_cases[4] = 0;
            #10;
            if (shift_ena !== 1) test_cases[4] = 0;
            #10;
            if (shift_ena !== 1) test_cases[4] = 0;
            #10;
            if (shift_ena !== 1) test_cases[4] = 0;
            #10;
            if (shift_ena !== 0) test_cases[4] = 0;
        end
    endtask

    task test_counting;
        begin
            if (counting !== 1) test_cases[3] = 0;
        end
    endtask

    task test_done;
        begin
            if (done !== 1) test_cases[2] = 0;
        end
    endtask

    task test_ack;
        begin
            if (!reset && ack) begin
                if (!(counting === 0 && done === 0 && shift_ena === 0)) test_cases[1] = 0;
            end
        end
    endtask

    task check_results;
        begin
            if (test_cases === 5'b11111) begin
                $display("all test cases passed");
            end
            $finish;
        end
    endtask
endmodule

