reg clk, enable, S, A, B, C;
wire Z;
integer i;

top_module dut (
    .clk(clk),
    .enable(enable),
    .S(S),
    .A(A),
    .B(B),
    .C(C),
    .Z(Z)
);

// Clock generation
initial begin
    clk = 0;
    forever #5 clk = ~clk; // Clock period is 10 ns
end

// Test cases
initial begin
    // Initialize all inputs
    enable = 0;
    S = 0;
    A = 0;
    B = 0;
    C = 0;

    // Resetting and filling shift register
    for (i = 0; i < 8; i = i + 1) begin
        @(posedge clk) begin
            enable = 1;
            S = i % 2; // Alternate pattern 0,1,0,1,...
        end
    end

