    forever #5 clk = ~clk;
end

initial begin
    reset = 1; in = 8'h00;
    #10 reset = 0;
    // Test Cases from Provided Waveform Example
    @(posedge clk) in = 8'h2c; reset = 0;
    @(posedge clk) in = 8'h81;
    @(posedge clk) in = 8'h09; #10;
    verify(8'h2c8109, 1);
    @(posedge clk) in = 8'h6b;
    @(posedge clk) in = 8'h0D;
    @(posedge clk) in = 8'h8D; #10;
    verify(8'h6b0d8d, 1);
    @(posedge clk) in = 8'h6D;
    @(posedge clk) in = 8'h12;
    @(posedge clk) in = 8'h01; #10;
    verify(8'h6d1201, 1);
    @(posedge clk) in = 8'hED;
    @(posedge clk) in = 8'h76;
    @(posedge clk) in = 8'h3D; #10;
    verify(8'hed763d, 1);
    @(posedge clk) in = 8'hED;
    @(posedge clk) in = 8'h8C;
    @(posedge clk) in = 8'hF9; #10;
    verify(8'hed8cf9, 1);
    @(posedge clk) in = 8'hCE;
    #10;
    $finish;
end

task verify;
    input [23:0] expected_bytes;
    input expected_done;
    begin
        if (out_bytes !== expected_bytes || done !== expected_done) begin
            $display("Test failed at time %t: expected %h, %b received %h, %b", $time, expected_bytes, expected_done, out_bytes, done);
        end
    end
endtask

// Remove $isunbounded() system task due to being undefined.
initial begin
    #50;
    if (done) begin
        $display("All test cases passed");
    end else begin
        $display("There was an error in test case completion.");
    end
end

endmodule

