module testbench;
    reg a, b, c, d;
    wire out;
    reg [7:0] pattern;
    reg [7:0] expected_output;
    reg [7:0] actual_output;
    integer i, errors;

    top_module DUT (
        .a(a), 
        .b(b), 
        .c(c), 
        .d(d), 
        .out(out)
    );

    initial begin
        // Test vector and expected output according to the Karnaugh map
        // Format: {a, b, c, d}
        // Output should be based on minimized function

        // Initializing
        errors = 0;

        // Test Patterns and Expected Results
        // The last four bits are the expected outputs assuming d as 0 for calculations
        //        inputs -> a b c d : expected output
        pattern          = 8'b0000_XXXX; // cd=00, ab=00 d-> don't care
        expected_output  = 8'bXXXX_0000; // Mapping all cases, d as don't care

        // Loop through all possible inputs (abcd)
        for (i = 0; i < 16; i = i + 1) begin
            {a, b, c, d} = i;

            // Setup delay for proper output stabilization
            #10;

            // Record actual output
            actual_output[i % 8] = out;

            // Produce output only if it is a defined expected output
            if (!(c == 0 && b == 1) | (pattern[i % 8] !== 1'bx)) begin
                if (actual_output[i % 8] !== expected_output[i % 8]) begin
                    errors = errors + 1;
                end
            end
        end

        // Check if there are no errors
        if (errors == 0) begin
            $display("all test cases passed");
        end
        $finish;
    end
endmodule

