module testbench;
    reg clk, areset, x;
    wire z;

    top_module DUT (
        .clk(clk),
        .areset(areset),
        .x(x),
        .z(z)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk;  // 100MHz clock
    end

    // Testcases
    initial begin
        // Initialize inputs
        areset = 1; x = 0; 
        #10; // Allow some time for the asynchronous reset

        areset = 0;
        // Test Case 1, State A - x=0 => State A, z should be 0
        x = 0; #10;
        if (z !== 0) $display("Test Case 1 Failed: Expected z=0, got z=%b", z);

        // Test Case 2, State A - x=1 => State B, z should be 1
        x = 1; #10;
        if (z !== 1) $display("Test Case 2 Failed: Expected z=1, got z=%b", z);

        // Test Case 3, State B - x=0 => State B, z should be 1
        x = 0; #10;
        if (z !== 1) $display("Test Case 3 Failed: Expected z=1, got z=%b", z);

        // Test Case 4, State B - x=1 => Stay in State B, z should be 0
        x = 1; #10;
        if (z !== 0) $display("Test Case 4 Failed: Expected z=0, got z=%b", z);

        // Reset and check for initial state condition
        areset = 1; #10;
        areset = 0; x = 0; #10;
        if (z !== 0) $display("Reset State Failed: Expected z=0 for State A");

        // If no error messages displayed, assume all tests passed
        #50; // Wait time for all tests to complete
        if (!$test$plusargs("error")) $display("all test cases passed");

        $finish;
    end
endmodule

