module testbench;
  reg a, b, c, d, e;
  wire [24:0] out;

  top_module DUT (
    .a(a),
    .b(b),
    .c(c),
    .d(d),
    .e(e),
    .out(out)
  );

  initial begin
    // Test vectors
    integer i;
    reg [4:0] test_vectors[0:31]; // Total 2^5 = 32 possible combinations for 5 inputs
    reg [24:0] expected_outputs[0:31];
    reg [24:0] computed_output;
    reg error_detected = 0;

    // Generate all possible combinations for a, b, c, d, e
    for (i = 0; i < 32; i = i + 1) begin
      test_vectors[i] = i[4:0];
    end

    // Expected outputs for all comparisons
    for (i = 0; i < 32; i = i + 1) begin
      expected_outputs[i][24] = ~(test_vectors[i][4] ^ test_vectors[i][4]);
      expected_outputs[i][23] = ~(test_vectors[i][4] ^ test_vectors[i][3]);
      expected_outputs[i][22] = ~(test_vectors[i][4] ^ test_vectors[i][2]);
      expected_outputs[i][21] = ~(test_vectors[i][4] ^ test_vectors[i][1]);
      expected_outputs[i][20] = ~(test_vectors[i][4] ^ test_vectors[i][0]);
      expected_outputs[i][19] = ~(test_vectors[i][3] ^ test_vectors[i][4]);
      expected_outputs[i][18] = ~(test_vectors[i][3] ^ test_vectors[i][3]);
      expected_outputs[i][17] = ~(test_vectors[i][3] ^ test_vectors[i][2]);
      expected_outputs[i][16] = ~(test_vectors[i][3] ^ test_vectors[i][1]);
      expected_outputs[i][15] = ~(test_vectors[i][3] ^ test_vectors[i][0]);
      expected_outputs[i][14] = ~(test_vectors[i][2] ^ test_vectors[i][4]);
      expected_outputs[i][13] = ~(test_vectors[i][2] ^ test_vectors[i][3]);
      expected_outputs[i][12] = ~(test_vectors[i][2] ^ test_vectors[i][2]);
      expected_outputs[i][11] = ~(test_vectors[i][2] ^ test_vectors[i][1]);
      expected_outputs[i][10] = ~(test_vectors[i][2] ^ test_vectors[i][0]);
      expected_outputs[i][ 9] = ~(test_vectors[i][1] ^ test_vectors[i][4]);
      expected_outputs[i][ 8] = ~(test_vectors[i][1] ^ test_vectors[i][3]);
      expected_outputs[i][ 7] = ~(test_vectors[i][1] ^ test_vectors[i][2]);
      expected_outputs[i][ 6] = ~(test_vectors[i][1] ^ test_vectors[i][1]);
      expected_outputs[i][ 5] = ~(test_vectors[i][1] ^ test_vectors[i][0]);
      expected_outputs[i][ 4] = ~(test_vectors[i][0] ^ test_vectors[i][4]);
      expected_outputs[i][ 3] = ~(test_vectors[i][0] ^ test_vectors[i][3]);
      expected_outputs[i][ 2] = ~(test_vectors[i][0] ^ test_vectors[i][2]);
      expected_outputs[i][ 1] = ~(test_vectors[i][0] ^ test_vectors[i][1]);
      expected_outputs[i][ 0] = ~(test_vectors[i][0] ^ test_vectors[i][0]);
    end

    // Run the test vectors
    for (i = 0; i < 32; i = i + 1) begin
      {a, b, c, d, e} = test_vectors[i];
      #10; // Wait for propagation
      computed_output = out;

      if (computed_output !== expected_outputs[i]) begin
        error_detected = 1;
        $display("Error with input vector %b. Expected %b, received %b", test_vectors[i], expected_outputs[i], computed_output);
      end
    end

    if (!error_detected) begin
      $display("all test cases passed");
    end

    $finish;
  end

endmodule

