module top_module_tb;
    // Inputs
    reg clk;
    reg slowena;
    reg reset;

    // Outputs
    wire [3:0] q;

    // Instantiate the Unit Under Test (UUT)
    top_module uut (
        .clk(clk),
        .slowena(slowena),
        .reset(reset),
        .q(q)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // Generate a clock with period 10ns
    end

    // Test sequences
    initial begin
        // Initialize Inputs
        slowena = 0;
        reset = 0;

        // Wait for the global reset
        #100;

        // Test Case 1: Reset the counter
        reset = 1; // Assert reset
        #10;
        reset = 0; // Deassert reset
        if (q !== 0) $display("Test Case 1 Failed");

        // Test Case 2: Check if counter holds value when slowena is low
        slowena = 0;
        #10; // Wait for a few clock cycles
        if (q !== 0) $display("Test Case 2 Failed");

        // Test Case 3: Normal counting with slowena high
        slowena = 1;
        #10; // Wait one clock cycle
        if (q !== 1) $display("Test Case 3 Failed"); // q should now be 1

        // Test for full counting sequence 0 to 9
        #80; // Wait 8 more clock cycles
        if (q !== 9) $display("Test Case 3.1 Failed");

        // Test Case 4: Overlap to 0 after 9
        #10; // Increment past 9
        if (q !== 0) $display("Test Case 4 Failed");

        // Test Case 5: Pause the counter
        slowena = 0;
        #10; // Wait for clock and check if value is still 0
        if (q !== 0) $display("Test Case 5 Failed");
        slowena = 1;

        // Poll test results to display "all test cases passed"
        #10; // Allow some time for errors to be displayed (if any)
        if (!$test$plusargs("Test Case 1 Failed") && !$test$plusargs("Test Case 2 Failed") && 
            !$test$plusargs("Test Case 3 Failed") && !$test$plusargs("Test Case 3.1 Failed") && 
            !$test$plusargs("Test Case 4 Failed") && !$test$plusargs("Test Case 5 Failed")) begin
            $display("all test cases passed");
        end

        $finish; // End simulation
    end
endmodule

