module testbench;
    reg clk;
    reg load;
    reg ena;
    reg [1:0] amount;
    reg [63:0] data;
    wire [63:0] q;

    // Instantiate the Device Under Test (DUT)
    top_module dut (
        .clk(clk),
        .load(load),
        .ena(ena),
        .amount(amount),
        .data(data),
        .q(q)
    );

    // Generate clock
    always #5 clk = ~clk;

    // Initial block for test cases
    initial begin
        // Initialize signals
        clk = 0;
        load = 0;
        ena = 0;
        amount = 2'b00;
        data = 64'b0;

        // Utilize task for performaning load and shift operations
        perform_test(1'b1, 64'hA5A5A5A5A5A5A5A5, 1'b1, 2'b00, 64'hA5A5A5A5A5A5A5A5);
        perform_test(1'b0, 64'h1234567890ABCDEF, 1'b1, 2'b00, 64'h4B48ACF121579BDE);
        perform_test(1'b0, 64'h0, 1'b1, 2'b01, 64'hCF121579BDE00000);
        perform_test(1'b0, 64'hFFFFFFFFFFFFFFFF, 1'b1, 2'b10, 64'hFFFFFFFFFFFFFFFF);
        perform_test(1'b0, 64'h0, 1'b1, 2'b11, 64'h00FFFFFFFFFFFFFF);

        // Perform a load during a shift operation (load should take precedence)
        perform_test(1'b1, 64'hF0F0F0F0F0F0F0F0, 1'b1, 2'b00, 64'hF0F0F0F0F0F0F0F0);

        // Check final output after all test cases, and display result
        if (q === 64'hF0F0F0F0F0F0F0F0) begin
            $display("all test cases passed");
        end

        $finish;
    end

    // Task to simulate load, enable, and shift operations
    task perform_test;
        input reg test_load;
        input reg [63:0] test_data;
        input reg test_ena;
        input reg [1:0] test_amount;
        input reg [63:0] expected_output;
        begin
            @(posedge clk); // Wait for positive edge of the clock
                load <= test_load;
                data <= test_data;
                ena <= test_ena;
                amount <= test_amount;
            @(posedge clk); // Wait for changes to take effect
                load <= 1'b0; // Clear load to perform shift on next cycles
            repeat (10) @(posedge clk);
                if (q !== expected_output) begin
                    $display("Test case failed: Expected %h, got %h", expected_output, q);
                end
        end
    endtask

endmodule

