module testbench;

    // DUT Inputs
    reg clk;
    reg resetn;
    reg x;
    reg y;

    // DUT Outputs
    wire f;
    wire g;

    // Instantiate the Device Under Test (DUT)
    top_module dut (
        .clk(clk),
        .resetn(resetn),
        .x(x),
        .y(y),
        .f(f),
        .g(g)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #10 clk = ~clk;
    end

    // Test cases
    initial begin
        // Initialize Inputs
        resetn = 1;
        x = 0;
        y = 0;

        // Reset the device
        #5 resetn = 0;
        #20 resetn = 1;

        // Test Case 1: f should be 1 for one cycle after reset
        @(posedge clk);
        if (f !== 1) $display("Test Case 1 Failed: f is not 1 after reset.");
        @(posedge clk);
        if (f !== 0) $display("Test Case 1 Failed: f did not return to 0.");

        // Test Case 2: Sequence x=1, x=0, x=1 then g should be 1
        @(posedge clk) x = 1;
        @(posedge clk) x = 0;
        @(posedge clk) x = 1;
        @(posedge clk);
        if (g !== 1) $display("Test Case 2 Failed: g is not 1 after x=1,0,1 sequence.");

        // Test Case 3: Check y within two cycles if g stays 1 if y=1
        @(posedge clk) y = 1;
        if (g !== 1) $display("Test Case 3 Failed: g did not stay 1 when y=1 within two cycles.");

        // Test Case 4: Check y within two cycles if g goes to 0 if y not 1
        @(posedge clk);
        @(posedge clk);
        resetn = 0;
        @(posedge clk) resetn = 1;
        @(posedge clk) x = 1;
        @(posedge clk) x = 0;
        @(posedge clk) x = 1;
        @(posedge clk);
        @(posedge clk) y = 0;
        @(posedge clk) y = 0;
        if (g !== 0) $display("Test Case 4 Failed: g did not go to 0 after y did not become 1 in two cycles.");

        // Check if all test cases passed
        if (!$test$plusargs("Test Case 1 Failed") && !$test$plusargs("Test Case 2 Failed") && !$test$plusargs("Test Case 3 Failed") && !$test$plusargs("Test Case 4 Failed"))
            $display("all test cases passed");

        // Finish simulation
        $finish;
    end

endmodule

